import React, { Component } from 'react';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import Modal from 'react-bootstrap/Modal'
import LoginForm from './LoginForm';
import RegisterForm from './RegisterForm';

class NavigationMenu extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            show: false
        }
    }

    render() {
        const handleClose = () => {
			this.setState({ show: false });
		};
		const handleShow = () => {
			this.setState({ show: true });
		};
        return (
            <>
                <div className="flex-row menu-container">
                    <div class="left flex-10">
                        <p>Our appp</p>
                    </div>
                    <div className="flex"></div>
                    <div class="right flex-10">
                        <button className="action-btn">Connect</button>
                    </div>
                </div>
                <Modal
                    show={this.state.show}
                    onHide={handleClose}
                    aria-labelledby="contained-modal-title-vcenter"
                    centered
                >
                    <Modal.Body>
                        <Tabs defaultActiveKey="login" id="uncontrolled-tab-example">
                            <Tab eventKey="login" title="Login">
                                <LoginForm handleClose={handleClose.bind(this)} />
                            </Tab>
                            <Tab eventKey="signup" title="Sign up">
                                <RegisterForm handleClose={handleClose.bind(this)} />
                            </Tab>
                        </Tabs>
                    </Modal.Body>
                </Modal>
            </>

        );
    }


}

export default NavigationMenu
